<?php//// Конттроллер страницы чтения.//include_once('m/model.php');include_once ('m/db.php');class C_Page extends C_Base{    //    // Конструктор.    //    public function action_index()    {        $this->title .= '::Главная';        $text = text_get();        $this->content = $this->Template('v/v_index.php', array('text' => $text));    }    public function action_catalog()    {        $this->title .= '::Каталог';        $goods = DB::getRows('SELECT * FROM products WHERE id < 4');        $this->content = $this->Template('v/v_catalog.php', array('goods' => $goods));    }    public function action_product($id)    {        $good = DB::getRow("SELECT * FROM products where id='$id'");        $this->title .= '::Один';        $this->content = $this->Template('v/v_product.php', array('good' => $good));    }    public function action_basket()    {    }}