<?php//// Контроллер страницы чтения.//include_once ('m/Product.php');class C_Page extends C_Base{    //    // Конструктор.    //    public function action_index()    {        $this->title .= '| Главная';        $product_object = new Product();        $products = $product_object->getThreeProducts();        $this->content = $this->Template('v/v_item.php', array('products' => $products));    }    public function action_catalog()    {        $this->title .= '| Каталог';        $product_object = new Product();        $products = $product_object->getThreeProducts();		$productsDB = $this->Template('v/v_item.php', array('products' => $products));        $this->content = $this->Template('v/v_catalog.php', array('products' => $productsDB));		    }    public function action_product($id)    {        $product_object = new Product();        $product = $product_object->getProduct($id);        //if (isset($_SESSION['user_id'])) {           // $user_id = $_SESSION['user_id'];       // } else {            //$user_id = false;        //}        $this->title .= ' | ' . $product['name'];		//$this->title .= '::Один';        $this->content = $this->Template('v/v_product.php', array('product' => $product));    }    //public function action_registration()    //{        //$this->title .= '| ht';        //$product_object = new Product();        //$products = $product_object->getThreeProducts();        //$this->content = $this->Template('v/v_registration.php', array());    //}    public function action_basket()    {    }}