<?php//// Контроллер страницы чтения.//include_once ('m/Product.php');include_once ('m/Basket.php');class C_Page extends C_Base{    public function action_index()    {		        $this->title .= '| Главная';        $product_object = new Product();        $products = $product_object->getThreeProducts();		        $this->content = $this->Template('v/v_item.php', array('products' => $products));    }    public function action_catalog()    {		$this->title .= '| Каталог';        $product_object = new Product();        $products = $product_object->getThreeProducts();		$productsDB = $this->Template('v/v_item.php', array('products' => $products));        $this->content = $this->Template('v/v_catalog.php', array('products' => $productsDB));    }    public function action_product($id)    {		$product_object = new Product();        $product = $product_object->getProduct($id);        $this->title .= ' | ' . $product['name'];		        $this->content = $this->Template('v/v_product.php', array('product' => $product));    }    public function action_basket()    {		$this->title .= '| Корзина';		if (isset($_SESSION['user_id'])) {			$user_id = $_SESSION['user_id'];			$basket_object = new Basket();			$basket = $basket_object->getBasket($user_id);			$this->content = $this->Template('v/v_basket.php', array('products' => $basket));		} else {			$this->library = '<script src="js/jquery.maskedinput.min.js"></script>								<script src="js/mainCheck.js"></script>';			$information_guest = $this->Template('v/v_guest.php', array());			if(isset($_COOKIE['guest']) === false) {				setcookie(guest,date("l dS of F Y h:I:s A"), time()+3600 * 24 * 30);				$this->content = $this->Template('v/v_basket.php', array(				'information_guest'=> $information_guest, 'library' => $library));							} else {				$cookie = $_COOKIE['guest'];				$basket_object_guest = new Basket();				$basket_guest = $basket_object_guest->getGuestBasket($cookie);				$this->content = $this->Template('v/v_basket.php', array('products' => $basket_guest,				'information_guest'=> $information_guest, 'library' => $library));			}		}    }}/*public function action_basket()    {		$this->title .= '| Корзина';		if (isset($_SESSION['user_id'])) {			$user_id = $_SESSION['user_id'];			$basket_object = new Basket();			$basket = $basket_object->getBasket($user_id);			$this->content = $this->Template('v/v_basket.php', array('products' => $basket));		}    }*/